import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

// 折叠按钮，展开与隐藏
function Btn(props) {
  let dom = props.isCollapse ?
    <span className="btn" onClick={() => props.onClick(false)}>more</span> :
    <span className="btn" onClick={() => props.onClick(true)}>collapse</span>
  return (
    dom
  )
}

class Card extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      isShowBtn: true,
      isCollapse: true,
      comments: props.comments,
    }
  }
  render() {
    const { isShowBtn, isCollapse, comments } = this.state

    return (
      <div className="comments-card">
      <div className="head">
        <span>00:00:00</span>
        <span className="grey">|</span>
        <span className="grey ellipsis text">Watch a free video course on Vue Mastery</span>
      </div>
      <div className="middle clearfix">
        <div className="left">
          <span className="avatar"></span>
        </div>
        <div className="right">
          <div>{this.props.name}</div>
          <div className="grey">1mins ago</div>
        </div>
      </div>
      <div ref={'info'} className={`info clearfix ${isShowBtn ? 'ellipsis_3' : ''}`}>
        {comments}
        {
          isShowBtn ? 
          <Btn 
            isCollapse={isCollapse} 
            onClick={(bool) => {this.hanlderBtn(bool)}}
          /> : ''
        }
      </div>
    </div>
    );
  }

  componentDidMount = () => {
    const cssStyles = { fontSize: '14px', fontWeight: '400', lineHeight: '24px' };
    this.isElementCollision(this.refs['info'], 3, cssStyles, true).then((isShowBtn) => {
      this.setState({
        isShowBtn: isShowBtn
      });
    })
    window.addEventListener('resize', () => {
      this.isElementCollision(this.refs['info'], 3, cssStyles, true).then((isShowBtn) => {
        this.setState({
          isShowBtn: isShowBtn
        });
      })
    })

  };

  hanlderBtn(bool) {
    this.setState({
      isCollapse: bool
    })
  }

  // 判断文本超出行数
  isElementCollision(ele, rowCount = 4, cssStyles, removeChild, offset = 0) {

    if (!ele) {
      return false;
    }

    const clonedNode = ele.cloneNode(true);
    // 给clone的dom增加样式
    clonedNode.style.overflow = 'visible';
    clonedNode.style.display = 'inline-block';
    clonedNode.style.width = 'auto';
    clonedNode.style.whiteSpace = 'nowrap';
    clonedNode.style.visibility = 'hidden';
    // 将传入的css字体样式赋值
    if (cssStyles) {
      Object.keys(cssStyles).forEach(item => {
        clonedNode.style[item] = cssStyles[item];
      });
    }

    // 给clone的dom增加id属性
    let _time = new Date().getTime();

    const containerID = 'collision_node_id_' + _time;
    clonedNode.setAttribute('id', containerID);

    let tmpNode = document.getElementById(containerID);
    let newNode = clonedNode;
    if (tmpNode) {
      document.body.replaceChild(clonedNode, tmpNode);
    } else {
      newNode = document.body.appendChild(clonedNode);
    }
    // 新增的dom宽度与原dom的宽度*限制行数做对比
    const differ = newNode.offsetWidth - ele.offsetWidth * rowCount - 30 - offset;
    console.log('differ', differ)
    if (removeChild) {
      document.body.removeChild(newNode);
    }
    return new Promise((resolve) => {
      resolve(differ > 0)
    })
  }

}

class Comments extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      list: [{
        name: 'Newton',
        comments: ''
      }],
    }
  }
  render() {
    const { list } = this.state

    return (
      <div className="comments">
      {
        list.map((item, i) => {
          return <Card
            key={i}
            name={item.name}
            comments={item.comments}
          />
        })
      }
      </div>
    );
  }

}

ReactDOM.render(
  <Comments />,
  document.getElementById('root')
);
